mapper.update:
update LIMS_CASE_INFO lc set lc.DELETE_FLAG = #{deleteFlag,jdbcType=VARCHAR},
lc.DELETE_DATETIME = #{deleteDatetime,jdbcType=TIMESTAMP},
lc.DELETE_PERSON = #{deletePerson,jdbcType=VARCHAR} where lc.case_id = #{caseId,jdbcType=VARCHAR}

mapper.select:
select count(lc.CASE_ID) from LIMS_CASE_INFO lc left join lims_consignment_info lci on lc.case_id = lci.case_id
where lc.delete_flag = '0' and lci.delete_flag = '0' and lci.APPEND_FLAG = '0'

    and lci.ACCEPT_ORG_ID = #{acceptOrgId, jdbcType=VARCHAR}


    and lci.status = #{status, jdbcType=VARCHAR}

select d.dict_name as name,nvl(t1.cnt,0) as value from dict_item d left join ( select t.case_property, count(t.case_id) as cnt from LIMS_CASE_INFO t left join lims_consignment_info lci on t.case_id=lci.case_id where t.delete_flag = '0'and lci.delete_flag = '0' and lci.APPEND_FLAG = '0' and (t.case_status = '02' or t.case_status = '03')
and lci.ACCEPT_ORG_ID = #{acceptOrgId, jdbcType=VARCHAR}
group by t.case_property) t1 on d.dict_code=t1.case_property where d.dict_type_code='CASE_PROPERTY' order by d.dict_code asc

select nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 01, count(lc.case_id))), 0) c1, nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 02, count(lc.case_id))), 0) c2, nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 03, count(lc.case_id))), 0) c3, nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 04, count(lc.case_id))), 0) c4, nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 05, count(lc.case_id))), 0) c5, nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 06, count(lc.case_id))), 0) c6, nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 07, count(lc.case_id))), 0) c7, nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 08, count(lc.case_id))), 0) c8, nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 09, count(lc.case_id))), 0) c9, nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 10, count(lc.case_id))), 0) c10, nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 11, count(lc.case_id))), 0) c11, nvl(MAX(decode(to_char(lc.create_datetime, 'MM'), 12, count(lc.case_id))), 0) c12 from LIMS_CASE_INFO lc where lc.delete_flag = '0'
and to_char(lc.create_datetime, 'yyyy') = #{year, jdbcType=VARCHAR}
group by to_char(lc.create_datetime, 'MM')

select * from ( select rownum as rn, t.* from ( select lc.CASE_ID, lc.CASE_NO, lc.MAJOR_TYPE, lc.MAJOR_NO, lc.CASE_XK_NO, lc.CASE_NAME, lc.CASE_BRIEF, lc.CASE_LOCATION, lc.CASE_DATETIME, lc.CASE_TYPE, lc.CASE_PROPERTY, lc.CASE_STATUS,lc.CASE_LEVEL, lc.CASE_REMARK, lc.CASE_URGENT_FLAG, lc.HAS_APPEND_FLAG, lc.FIRST_CHECKER, lc.SERVER_NO, lc.INSTORED_FLAG, lc.CREATE_DATETIME, lc.CREATE_PERSON, lc.UPDATE_DATETIME, lc.UPDATE_PERSON, lc.DELETE_FLAG, lc.DELETE_DATETIME, lc.DELETE_PERSON, lc.DELETE_REASON, lc.IS_HOMICIDE_FLAG, lc.IS_CRIMINAL_FLAG, lc.RESULT_QUERY_SN, lc.RESULT_QUERY_CAPTCHA, di_2.dict_name as CASE_TYPE_NAME, di_1.dict_name as CASE_PROPERTY_NAME, di_3.dict_name as CASE_STATUS_NAME, lci.CONSIGNMENT_ID as CONSIGNMENT_ID, lci.CONSIGNMENT_NO as CONSIGNMENT_NO, lci.DELEGATOR1_ID as DELEGATOR1_ID, lci.DELEGATOR1_NAME as DELEGATOR1_NAME, lci.DELEGATOR2_ID as DELEGATOR2_ID, lci.DELEGATOR2_NAME as DELEGATOR2_NAME, lci.DELEGATE_ORG_CODE as DELEGATE_ORG_CODE, lci.DELEGATE_ORG_NAME as DELEGATE_ORG_NAME, lci.DELEGATE_DATETIME as DELEGATE_DATETIME, lci.STATUS as STATUS, lci.ACCEPT_DATETIME as ACCEPT_DATETIME, lci.APPEND_FLAG as APPEND_FLAG, lci.acceptor_id as ACCEPTOR_ID from lims_case_info lc left join lims_consignment_info lci on lci.CASE_ID = lc.case_id left join dict_item di_1 on di_1.dict_code = lc.CASE_PROPERTY and di_1.DICT_TYPE_CODE='CASE_PROPERTY' left join dict_item di_2 on di_2.dict_code = lc.CASE_TYPE and di_2.DICT_TYPE_CODE='CASE_TYPE' left join dict_item di_3 on di_3.dict_code = lc.CASE_STATUS and di_3.DICT_TYPE_CODE='CASE_STATUS' where lci.delete_flag = '0' and lc.delete_flag = '0'
order by ${orderByClause}
) t where rownum <=#{rows} ) where rn >#{offset}

select * from ( select rownum as rn, t.* from ( select lc.CASE_ID, lc.CASE_NO, lc.MAJOR_TYPE, lc.MAJOR_NO, lc.CASE_XK_NO, lc.CASE_NAME, lc.CASE_BRIEF, lc.CASE_LOCATION, lc.CASE_DATETIME, lc.CASE_TYPE, lc.CASE_PROPERTY, lc.CASE_STATUS,lc.CASE_LEVEL, lc.CASE_REMARK, lc.CASE_URGENT_FLAG, lc.HAS_APPEND_FLAG, lc.FIRST_CHECKER, lc.SERVER_NO, lc.INSTORED_FLAG, lc.CREATE_DATETIME, lc.CREATE_PERSON, lc.UPDATE_DATETIME, lc.UPDATE_PERSON, lc.DELETE_FLAG, lc.DELETE_DATETIME, lc.DELETE_PERSON, lc.DELETE_REASON, lc.IS_HOMICIDE_FLAG, lc.IS_CRIMINAL_FLAG, lc.RESULT_QUERY_SN, lc.RESULT_QUERY_CAPTCHA, di_2.dict_name as CASE_TYPE_NAME, di_1.dict_name as CASE_PROPERTY_NAME, di_3.dict_name as CASE_STATUS_NAME, lci.CONSIGNMENT_ID as CONSIGNMENT_ID, lci.CONSIGNMENT_NO as CONSIGNMENT_NO, lci.DELEGATOR1_ID as DELEGATOR1_ID, lci.DELEGATOR1_NAME as DELEGATOR1_NAME, lci.DELEGATOR2_ID as DELEGATOR2_ID, lci.DELEGATOR2_NAME as DELEGATOR2_NAME, lci.DELEGATE_ORG_CODE as DELEGATE_ORG_CODE, lci.DELEGATE_ORG_NAME as DELEGATE_ORG_NAME, lci.DELEGATE_DATETIME as DELEGATE_DATETIME, lci.STATUS as STATUS, lci.ACCEPT_DATETIME as ACCEPT_DATETIME, lci.APPEND_FLAG as APPEND_FLAG, lci.acceptor_id as ACCEPTOR_ID from lims_case_info lc left join lims_consignment_info lci on lci.CASE_ID = lc.case_id left join dict_item di_1 on di_1.dict_code = lc.CASE_PROPERTY and di_1.DICT_TYPE_CODE='CASE_PROPERTY' left join dict_item di_2 on di_2.dict_code = lc.CASE_TYPE and di_2.DICT_TYPE_CODE='CASE_TYPE' left join dict_item di_3 on di_3.dict_code = lc.CASE_STATUS and di_3.DICT_TYPE_CODE='CASE_STATUS' where lci.delete_flag = '0' and lc.delete_flag = '0'
order by ${orderByClause}
) t where rownum <=#{rows} ) where rn >#{offset}

SELECT
COUNT( (SELECT g.AUDITED_GENE_ID FROM MATCH_AUDITED_GENE g  WHERE g.SAMPLE_ID = DD.sample_id) ) AS getExamine
FROM (SELECT d.SAMPLE_ID  FROM LIMS_SAMPLE_INFO_DNA d WHERE d.CASE_ID = #{caseid}


    AND d.INSTORED_FLAG =#{type}

) dd

select * from ( select rownum as rn, t.* from ( select distinct lc.CASE_NO,lc.CASE_ID,lc.MAJOR_TYPE, lc.MAJOR_NO, lc.CASE_XK_NO, lc.CASE_NAME, lc.CASE_BRIEF, lc.CASE_LOCATION, lc.CASE_DATETIME, lc.CASE_TYPE, lc.CASE_PROPERTY, lc.CASE_STATUS,lc.CASE_LEVEL, lc.CASE_REMARK, lc.CASE_URGENT_FLAG, lc.HAS_APPEND_FLAG, lc.FIRST_CHECKER, lc.SERVER_NO, lc.INSTORED_FLAG, lc.CREATE_DATETIME, lc.CREATE_PERSON, lc.UPDATE_DATETIME, lc.UPDATE_PERSON, lc.DELETE_FLAG, lc.DELETE_DATETIME, lc.DELETE_PERSON, lc.DELETE_REASON, lc.IS_HOMICIDE_FLAG, lc.IS_CRIMINAL_FLAG, lc.RESULT_QUERY_SN, lc.RESULT_QUERY_CAPTCHA,lc.GJK_SYS_NO from lims_case_info lc left join lims_consignment_info lci on lci.CASE_ID = lc.CASE_ID left join lims_sample_info_dna si on si.case_id=lc.case_id and si.instored_flag='1' where lc.delete_flag = '0' and lci.APPEND_FLAG='0'
and lci.CASE_ID = #{caseId,jdbcType=VARCHAR}
and lc.CASE_ID in (select distinct(qs.foreign_id) from queue_sample qs where qs.foreign_id = si.case_id and qs.queue_type='15')
and lc.CASE_ID in (select distinct(si.case_id) from queue_sample qs where qs.foreign_id = si.sample_no and qs.queue_type='16')
and exists (select 1 from lims_sample_info_dna si where si.case_id=lc.case_id and si.SAMPLE_NO=#{sampleNo, jdbcType=VARCHAR})
order by lc.case_no desc nulls last ) t where rownum <=#{rows} ) where rn >#{offset}

SELECT count(distinct lci.CONSIGNMENT_ID)
from lims_case_info lc
left join lims_consignment_info lci on lci.CASE_ID = lc.case_id
where lci.DELETE_FLAG='0' and lc.DELETE_FLAG = '0'


        and lci.ACCEPTOR_ID = #{acceptorId,jdbcType=VARCHAR}


        and lci.APPEND_FLAG = #{appendFlag,jdbcType=VARCHAR}


        and lci.ACCEPT_DATETIME >= #{acceptStartDatetime,jdbcType=TIMESTAMP}


        and lci.ACCEPT_DATETIME <= #{acceptEndDatetime,jdbcType=TIMESTAMP}



        and lc.CASE_PROPERTY = #{entity.caseProperty,jdbcType=VARCHAR}

SELECT count(distinct lci.CONSIGNMENT_ID) from lims_case_info lc
left join lims_consignment_info lci on lci.CASE_ID = lc.case_id
where lci.DELETE_FLAG='0' and lc.DELETE_FLAG = '0'
and lc.instored_flag = '1'


        and lci.ACCEPTOR_ID = #{acceptorId,jdbcType=VARCHAR}


        and lci.ACCEPT_DATETIME >= #{acceptStartDatetime,jdbcType=TIMESTAMP}


        and lci.ACCEPT_DATETIME <= #{acceptEndDatetime,jdbcType=TIMESTAMP}



        and lc.CASE_PROPERTY = #{entity.caseProperty,jdbcType=VARCHAR}

select * from (
select rownum as rn, t.* from (
    select lci.*
    from LIMS_CASE_INFO lci
    where  lci.delete_flag = '0'
    order by lci.create_datetime desc
) t where rownum <=#{rows}
) where rn >#{offset}
