mapper.update:
update lims_sample_gene
set AUDIT_STATUS ='0'
where SAMPLE_ID = #{sampleId,jdbcType=VARCHAR}

update LIMS_SAMPLE_GENE
set
  AUDIT_STATUS = #{auditStatus,jdbcType=VARCHAR}
where GENE_ID = #{geneId,jdbcType=VARCHAR}

update LIMS_SAMPLE_GENE
set UPDATE_DATETIME = #{updateDatetime,jdbcType=TIMESTAMP},
UPDATE_PERSON = #{updatePerson,jdbcType=VARCHAR},
REVIEW_STATUS = #{reviewStatus,jdbcType=VARCHAR}
where GENE_ID = #{geneId,jdbcType=VARCHAR}

mapper.select:
select lsg.*, lsg.BOARD_NO as boardNo, lsid.SAMPLE_NO as sampleNo, lsid.SAMPLE_FLAG as sampleFlag, lsid.SAMPLE_NAME as sampleName from LIMS_SAMPLE_GENE lsg left join LIMS_SAMPLE_INFO_DNA lsid on lsid.SAMPLE_ID = lsg.SAMPLE_ID where lsid.DELETE_FLAG='0' and lsg.audit_status='1'
and lsg.GENE_TYPE = #{geneType,jdbcType=VARCHAR}
and lsid.CASE_ID = #{caseId,jdbcType=VARCHAR}
order by lsid.SAMPLE_NO asc
